mapper.select:
SELECT T1.ID, T1.USERNAME, T1.PASSWORD, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION, T2.REAL_NAME, T2.SEX, T2.BIRTHDAY, T2.TELEPHONE, T2.EMAIL, T2.QQ, T2.WX, T2.WEI_BO, T2.LEVEL FROM mdc_account T1 LEFT JOIN mdc_user T2 ON T1.USER_ID = T2.ID
T1.USERNAME = #{username}
AND T2.ID = #{userId}
AND T1.STATUS = 1

SELECT ID, USERNAME, PASSWORD, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION FROM mdc_account WHERE ID IN
#{item}
AND STATUS = 1

SELECT T1.ID, T1.USERNAME, T1.PASSWORD, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION, T2.REAL_NAME, T2.SEX, T2.BIRTHDAY, T2.TELEPHONE, T2.EMAIL, T2.QQ, T2.WX, T2.WEI_BO, T2.LEVEL FROM mdc_account T1 LEFT JOIN mdc_user T2 ON T1.USER_ID = T2.ID
T1.USERNAME = #{username}
AND T2.ID = #{user.id}
AND T2.REAL_NAME like concat('%',#{user.realName}, '%')
AND STATUS = 1

mapper.update:
UPDATE mdc_account
PASSWORD = #{password}, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

mapper.delete:
UPDATE mdc_account
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE mdc_account
STATUS = 0, MODIFIER = #{item.modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}
