mapper.select:
SELECT T1.ID, T1.CODE, T1.NAME, T1.FULL_CODE, T1.FULL_NAME, T1.LEVEL, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION, T2.ID AS ORGANIZATION_ID, T2.CODE AS ORGANIZATION_CODE, T2.NAME AS ORGANIZATION_CODE, T2.FULL_CODE AS ORGANIZATION_FULL_CODE, T2.FULL_NAME AS ORGANIZATION_FULL_NAME, T2.LEVEL AS ORGANIZATION_LEVEL FROM MDC_ORGANIZATION T1 LEFT JOIN MDC_ORGANIZATION T2 ON T1.ORGANIZATION_ID = T2.ID
AND T1.CODE = #{code}
T1.NAME LIKE CONCAT('%',#{name},'%')
AND T1.ORGANIZATION_ID = #{organization.id}

mapper.delete:
UPDATE MDC_ORGANIZATION
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE MDC_ORGANIZATION
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}
