mapper.select:
SELECT ID, CODE, NAME, FULL_CODE, FULL_NAME, LEVEL, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION FROM mdc_organization WHERE ID IN
#{item}
AND STATUS = 1

SELECT T1.ID, T1.CODE, T1.NAME, T1.FULL_CODE, T1.FULL_NAME, T1.LEVEL, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION, T1.ORGANIZATION_ID, T2.CODE AS ORGANIZATION_CODE, T2.NAME AS ORGANIZATION_NAME, T1.CORPORATION_ID, T3.NAME AS CORPORATION_NAME, T1.USER_ID, T4.REAL_NAME FROM mdc_organization T1 LEFT JOIN mdc_organization T2 ON T1.ORGANIZATION_ID = T2.ID INNER JOIN mdc_corporation T3 ON T1.CORPORATION_ID = T3.ID AND T3.STATUS = 1 LEFT JOIN mdc_user T4 ON T1.USER_ID = T4.ID
AND T1.NAME LIKE CONCAT('%',#{name},'%')
AND T1.FULL_CODE LIKE CONCAT('%', #{fullCode}, '%')
AND T1.ORGANIZATION_ID = #{parentId}
AND T3.CODE = #{corporationCode}
AND T1.STATUS = 1

mapper.delete:
UPDATE mdc_organization
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE mdc_organization
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}
