mapper.select:
SELECT ID, CODE, NAME, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION FROM mdc_role WHERE ID IN
#{item}
AND STATUS = 1

SELECT T1.ID, T1.CODE, T1.NAME, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION FROM mdc_role T1
INNER JOIN MDC_ACCOUNT_ROLE T2 ON T1.ID = T2.ROLE_ID INNER JOIN MDC_ACCOUNT T3 ON T2.ACCOUNT_ID = T3.ID
T1.NAME LIKE CONCAT('%',#{name},%);
AND T3.USERNAME = #{username}
AND T1.STATUS = 1

mapper.insert:
INSERT INTO mdc_role(
ID, CODE, NAME, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION
) VALUES (
#{id},
#{code},
#{name},
#{status},
#{creator},
now(),
#{modifier},
now(),
#{version}
);

mapper.update:
UPDATE mdc_role
CODE = #{code}, NAME = #{name}, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

update.foreach:
UPDATE mdc_role
CODE = #{item.code}, NAME = #{item.name}, MODIFIER = #{item.modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}

mapper.delete:
UPDATE mdc_role
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE mdc_role
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}

mapper.insert:
INSERT INTO MDC_ACCOUNT_ROLE(
ACCOUNT_ID,
ROLE_ID
) VALUES

    (
    #{item.id},
    #{id}
    )
