mapper.select:
SELECT T1.ID, T1.CODE, T1.NAME, T1.STATUS, T1.CREATOR, T1.CREATED,T1.MODIFIER, T1.MODIFIED, T1.VERSION
FROM MDC_ROLE T1
WHERE T1.ID = #{id}

SELECT T1.ID, T1.CODE, T1.NAME, T1.STATUS, T1.CREATOR, T1.CREATED,T1.MODIFIER, T1.MODIFIED, T1.VERSION
FROM MDC_ROLE T1
WHERE T1.CODE = #{code}

SELECT T1.ID, T1.CODE, T1.NAME, T1.STATUS, T1.CREATOR, T1.CREATED,T1.MODIFIER, T1.MODIFIED, T1.VERSION
FROM MDC_ROLE T1

    INNER JOIN MDC_ACCOUNT_ROLE T2 ON T1.ID = T2.ROLE_ID
    INNER JOIN MDC_ACCOUNT T3 ON T2.ACCOUNT_ID = T3.ID



        T1.NAME LIKE CONCAT('%',#{name},%);


        AND T3.USERNAME = #{username}

mapper.update:
UPDATE MDC_ROLE
CODE = #{code}, NAME = #{name}, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

update.foreach:
UPDATE MDC_ROLE
CODE = #{item.code}, NAME = #{item.name}, MODIFIER = #{item.modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}

mapper.delete:
UPDATE MDC_ROLE
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE MDC_ROLE
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}

mapper.insert:
INSERT INTO MDC_ACCOUNT_ROLE(
ACCOUNT_ID,
ROLE_ID
) VALUES

    (
    #{item.id},
    #{id}
    )
