mapper.select:
SELECT T1.ID, T1.REAL_NAME, T1.SEX, T1.BIRTHDAY, T1.TELEPHONE, T1.EMAIL, T1.QQ, T1.WX,
T1.WEI_BO,T1.PHOTO, T1.LEVEL, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION
FROM MDC_USER T1


        T1.REAl_NAME LIKE CONCAT('%',#{realName},'%')


        AND T1.EMAIL = #{email}


        AND T1.TELEPHONE = #{telephone}


        AND T1.QQ = #{qq}


        AND T1.WX = #{wx}


        AND T1.WEI_BO = #{weiBo}

SELECT T1.ID, T1.REAL_NAME, T1.SEX, T1.BIRTHDAY, T1.TELEPHONE, T1.EMAIL, T1.QQ, T1.WX,
T1.WEI_BO,T1.PHOTO, T1.LEVEL, T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION
FROM MDC_USER T1


        T1.EMAIL = #{email}


        AND T1.TELEPHONE = #{telephone}


        AND T1.QQ = #{qq}


        AND T1.WX = #{wx}


        AND T1.WEI_BO = #{weiBo}

mapper.update:
UPDATE MDC_USER
REAl_NAME = #{realName}, SEX = #{sex}, BIRTHDAY = #{birthday}, TELEPHONE = #{telephone}, EMAIL = #{email}, QQ = #{qq}, WX = #{wx}, WEI_BO = #{weiBo}, PHOTO = #{photo}, LEVEL = #{level}, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

update.foreach:
UPDATE MDC_USER
REAl_NAME = #{item.realName}, SEX = #{item.sex}, BIRTHDAY = #{item.birthday}, TELEPHONE = #{item.telephone}, EMAIL = #{item.email}, QQ = #{item.qq}, WX = #{item.wx}, WEI_BO = #{item.weiBo}, PHOTO = #{item.photo}, LEVEL = #{item.level}, MODIFIER = #{item.modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}

mapper.delete:
UPDATE MDC_USER
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE MDC_USER
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}
