mapper.select:
SELECT
ID, REAL_NAME, SEX, BIRTHDAY, TELEPHONE, EMAIL, QQ, WX, WEI_BO, PHOTO, LEVEL, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION
FROM mdc_user


        EMAIL = #{email}


        AND TELEPHONE = #{telephone}


        AND QQ = #{qq}


        AND WX = #{wx}


        AND WEI_BO = #{weiBo}

    AND STATUS = 1

SELECT ID, REAL_NAME, SEX, BIRTHDAY, TELEPHONE, EMAIL, QQ, WX, WEI_BO, PHOTO, LEVEL, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION FROM mdc_user WHERE ID IN
#{item}
AND STATUS = 1

SELECT
ID, REAL_NAME, SEX, BIRTHDAY, TELEPHONE, EMAIL, QQ, WX, WEI_BO, PHOTO, LEVEL, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION
FROM mdc_user


        REAl_NAME LIKE CONCAT('%',#{realName},'%')


        AND EMAIL = #{email}


        AND TELEPHONE = #{telephone}


        AND QQ = #{qq}


        AND WX = #{wx}


        AND WEI_BO = #{weiBo}

    AND STATUS = 1

mapper.update:
UPDATE mdc_user
REAl_NAME = #{realName}, SEX = #{sex}, BIRTHDAY = #{birthday}, TELEPHONE = #{telephone}, EMAIL = #{email}, QQ = #{qq}, WX = #{wx}, WEI_BO = #{weiBo}, PHOTO = #{photo}, LEVEL = #{level}, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

update.foreach:
UPDATE mdc_user
REAl_NAME = #{item.realName}, SEX = #{item.sex}, BIRTHDAY = #{item.birthday}, TELEPHONE = #{item.telephone}, EMAIL = #{item.email}, QQ = #{item.qq}, WX = #{item.wx}, WEI_BO = #{item.weiBo}, PHOTO = #{item.photo}, LEVEL = #{item.level}, MODIFIER = #{item.modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}

mapper.delete:
UPDATE mdc_user
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE mdc_user
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}
