mapper.select:
SELECT
T1.ID, T1.REAL_NAME, T1.SEX, T1.BIRTHDAY, T1.TELEPHONE, T1.EMAIL, T1.QQ, T1.WX, T1.WEI_BO, T1.PHOTO, T1.LEVEL,
T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION, T1.CORPORATION_ID, T1.ORGANIZATION_ID,
T2.NAME AS CORPORATION_ID, T3.NAME AS ORGANIZATION_NAME
FROM mdc_user T1
INNER JOIN mdc_corporation T2 ON T1.CORPORATION_ID = T2.ID AND T2.STATUS = 1
INNER JOIN mdc_organization T3 ON T1.ORGANIZATION_ID = T3.ID AND T3.STATUS = 1
FROM mdc_user T1


        T1.EMAIL = #{email}


        AND T1.TELEPHONE = #{telephone}


        AND T1.QQ = #{qq}


        AND T1.WX = #{wx}


        AND T1.WEI_BO = #{weiBo}

    AND T1.STATUS = 1

SELECT ID, REAL_NAME, SEX, BIRTHDAY, TELEPHONE, EMAIL, QQ, WX, WEI_BO, PHOTO, LEVEL, STATUS, CREATOR, CREATED, MODIFIER, MODIFIED, VERSION FROM mdc_user WHERE ID IN
#{item}
AND STATUS = 1

SELECT
T1.ID, T1.REAL_NAME, T1.SEX, T1.BIRTHDAY, T1.TELEPHONE, T1.EMAIL, T1.QQ, T1.WX, T1.WEI_BO, T1.PHOTO, T1.LEVEL,
T1.STATUS, T1.CREATOR, T1.CREATED, T1.MODIFIER, T1.MODIFIED, T1.VERSION, T1.CORPORATION_ID, T1.ORGANIZATION_ID,
T2.NAME AS CORPORATION_ID, T3.NAME AS ORGANIZATION_NAME
FROM mdc_user T1
INNER JOIN mdc_corporation T2 ON T1.CORPORATION_ID = T2.ID AND T2.STATUS = 1
INNER JOIN mdc_organization T3 ON T1.ORGANIZATION_ID = T3.ID AND T3.STATUS = 1


        T1.REAl_NAME LIKE CONCAT('%',#{realName},'%')


        AND T1.EMAIL = #{email}


        AND T1.TELEPHONE = #{telephone}


        AND T1.QQ = #{qq}


        AND T1.WX = #{wx}


        AND T1.WEI_BO = #{weiBo}


        AND T1.CORPORATION_ID = #{corporationId}


        AND T1.ORGANIZATION_ID = #{organizationId}

    AND T1.STATUS = 1

mapper.update:
UPDATE mdc_user
REAl_NAME = #{realName}, SEX = #{sex}, BIRTHDAY = #{birthday}, TELEPHONE = #{telephone}, EMAIL = #{email}, QQ = #{qq}, WX = #{wx}, WEI_BO = #{weiBo}, PHOTO = #{photo}, LEVEL = #{level},
CORPORATION_ID = #{corporation.id}
ORGANIZATION_ID = #{organization.id}
MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

mapper.delete:
UPDATE mdc_user
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{version} + 1
WHERE ID = #{id} AND VERSION = #{version}

delete.foreach:
UPDATE mdc_user
STATUS = 0, MODIFIER = #{modifier}, MODIFIED = now(), VERSION = #{item.version} + 1
WHERE ID = #{item.id} AND VERSION = #{item.version}
